<script>
import { onMounted } from '@vue/runtime-core'
import itemAlert from '../src/components/itemBought.vue'
import gsap from 'gsap'

export default{
  components:{ 
    itemAlert
  },
  data(){
    return{
      isMobileNavOpen: false,
      basketItems: 0,
      basketPulse: false,
      buyAlert: false,
      basketOccupied: false,
      processingAlert: false,
      darkMode: false,
      maxAmountItems: 10,
      warningPromptOpen: false,

      finalPrice: null,
      finalItems: [],
      finalPaymentMethod: null,

      scrolledDown: false,
      userAgent: false,

      footerInfoShow: false,

      tempMobile: null
      
    }
  },
  methods:{
    handleMobile(){
      this.isMobileNavOpen = !this.isMobileNavOpen
      this.tempMobile = null
      if(this.scrolledDown === true){
        this.tempMobile = this.scrolledDown
        this.scrolledDown = false
      }else{

      }

      if(this.isMobileNavOpen === false){
        if(this.tempMobile = true){
          this.scrolledDown = true;
          this.tempMobile = false;
        }
      }
    },
    getBasket(params){
      var self = this;
      let prevBasket = self.basketItems
      self.basketItems = 0;
      // console.log("Basket Running");
      Object.keys(sessionStorage).forEach(function(key){
        // console.log(key);
        if(key.includes("itemId-")){
          self.basketItems += parseInt(sessionStorage.getItem(key))
          // console.log(self.basketItems)
        }
      });
      if(params === true){
        prevBasket = self.basketItems;
      }
      if(self.basketItems > prevBasket){
        self.basketPulse = true;
        self.prevBasket = self.basketItems;
      }
      if(self.basketItems > 0){
        self.basketOccupied = true;
      }else{
        self.basketOccupied = false;
      };
      setTimeout(() =>{
        self.basketPulse = false;
      },6000)
    },
    buyAlertReset(){
      var self = this;
      self.buyAlert = false;
      self.processingAlert = false;
      console.log("BA2")
    },
    itemBoughtAlert(){
      var self  = this;
      self.buyAlert = false;
      self.buyAlert = true;
      setTimeout(function(){self.buyAlertReset()},5000)
    },
    componentGrabBasketItems(){
      var self = this;
      var arrayToPush = [];
      // console.log("Basket (Component) Running");
      Object.keys(sessionStorage).forEach(function(key){
        // console.log(key);
        if(key.includes("itemId-")){
          var quantity = sessionStorage.getItem(key)
          arrayToPush.push({itemKey: key, itemQuantity: parseInt(quantity)})
        }
      });
      // console.log(arrayToPush)
      return arrayToPush;
    },
    basketItemsPing(){
      var self = this;
      return self.basketItems;
    },
    getDarkMode(){
      if(localStorage.getItem('mode')){
        this.darkMode = localStorage.getItem('mode');
        document.body.classList.add(this.darkMode);
      }else{
        localStorage.setItem('mode', 'dark');
        this.darkMode = localStorage.getItem('mode');
        document.body.classList.add(this.darkMode);
      }
    },
    handleScroll(event){
      if(window.pageYOffset > 20 || document.documentElement.scrollTop > 20){
        if(this.isMobileNavOpen != true){
          this.scrolledDown = true;
        }
      }else{
        this.scrolledDown = false;
      }
    },
    goToTop(){
      window.scroll(0,0)
    }
  },
  created(){
    window.addEventListener('scroll',this.handleScroll)
  },
  destroyed(){
    window.removeEventListener('scroll',this.handleScroll)
  },
  mounted(){
    var self = this;
    self.getBasket(true);
    self.getDarkMode();

  },
}
</script>

